function generateRandomNumbers() {
  const numbers = [];
  for (let i = 0; i < 5000; i++) {
    numbers.push(Math.floor(Math.random() * 101)); // returns a float between 0 and 100
  }
  return numbers;
}

var grades = generateRandomNumbers();

